å®¢æˆ·					this.isSupervisor = userRole === 'supervisor'					this.isMaterialSupplier = userRole === 'material_supplier'										console.log('ğŸ¯ ç”¨æˆ·èº«ä»½è®¾ç½®:', {						role: this.userRole,						roleName: this.currentRoleConfig.name,						isDesigner: this.isDesigner,						isCustomer: this.isCustomer,						isSupervisor: this.isSupervisor,						isMaterialSupplier: this.isMaterialSupplier					})										return true				} catch (error) {					console.error('âŒ è·å–ç”¨æˆ·èº«ä»½å¤±è´¥:', error)					// é»˜è®¤è®¾ç½®ä¸ºæ™®é€šç”¨æˆ·èº«ä»½					this.userRole = 'user'					this.currentRoleConfig = ROLE_CONFIG['user']					this.isCustomer = true					this.isDesigner = false					this.isSupervisor = false					this.isMaterialSupplier = false					return false				}			},						// è·å–ç©ºçŠ¶æ€æè¿°			getEmptyDesc() {				const descMap = {					'user': 'æ‚¨è¿˜æ²¡æœ‰ä»»ä½•è®¢å•',					'designer': 'æ‚¨è¿˜æ²¡æœ‰æ¥åˆ°çš„è®¢å•',					'supervisor': 'æ‚¨è¿˜æ²¡æœ‰ç›‘ç†çš„è®¢å•',					'material_supplier': 'æ‚¨è¿˜æ²¡æœ‰ææ–™è®¢å•'				}				return descMap[this.userRole] || 'æš‚æ— è®¢å•'			},						// è·å–å½“å‰ç”¨æˆ·ä¿¡æ¯			async getCurrentUserInfo() {				try {					console.log('ğŸ‘¤ å¼€å§‹è·å–ç”¨æˆ·ä¿¡æ¯...')										// å…ˆè·å–èº«ä»½ä¿¡æ¯					await this.getCurrentRole()										const userInfo = await getUserProfile()					console.log('âœ… ç”¨æˆ·ä¿¡æ¯è·å–æˆåŠŸ:', userInfo)										// æ ¹æ®å®é™…æ¥å£è¿”å›ç»“æ„è°ƒæ•´					let userId = null					if (userInfo && userInfo.userId) {						userId = userInfo.userId					} else if (userInfo && userInfo.data && userInfo.data.userId) {						userId = userInfo.data.userId					} else if (userInfo && userInfo.id) {						userId = userInfo.id					}										if (userId) {						this.currentUserId = userId						console.log('ğŸ¯ è®¾ç½®å½“å‰ç”¨æˆ·ID:', this.currentUserId)						return true					} else {						console.error('âŒ æœªæ‰¾åˆ°ç”¨æˆ·ID')						uni.showToast({							title: 'è·å–ç”¨æˆ·ä¿¡æ¯å¤±è´¥',							icon: 'no