				if (!timeStr) return ''				if (typeof timeStr === 'number') {					const date = new Date(timeStr)					return date.toLocaleDateString()				}				return timeStr.split(' ')[0]			},						// æ ¼å¼åŒ–æ—¥æœŸ			formatDate(dateStr) {				if (!dateStr) return ''				if (dateStr.includes('T')) {					return dateStr.split('T')[0]				}				return dateStr.split(' ')[0]			},						// æ ¹æ®é¡¹ç›®IDè·å–é¡¹ç›®è¯¦æƒ…			async getProjectDetail(projectId) {				if (!projectId) {					console.warn('é¡¹ç›®IDä¸ºç©º')					return null				}								try {					console.log('ğŸ“‹ è·å–é¡¹ç›®è¯¦æƒ…ï¼Œé¡¹ç›®ID:', projectId)					const projectDetail = await projectService.getProjectDetail(projectId)					console.log('âœ… é¡¹ç›®è¯¦æƒ…è·å–æˆåŠŸ:', projectDetail)					return projectDetail				} catch (error) {					console.error('âŒ è·å–é¡¹ç›®è¯¦æƒ…å¤±è´¥:', error)					return null				}			},						// åŠ è½½è®¢å•åˆ—è¡¨			async loadOrderList() {				if (this.loading) return								// æ£€æŸ¥ç”¨æˆ·ID				if (!this.currentUserId) {					console.log('ğŸ”„ æœªè·å–åˆ°ç”¨æˆ·IDï¼Œå…ˆè·å–ç”¨æˆ·ä¿¡æ¯...')					const hasUserInfo = await this.getCurrentUserInfo()					if (!hasUserInfo) {						this.loading = false						this.refreshing = false						return					}				}								try {					this.loading = true										const queryParams = {						pageNum: this.pagination.pageNum,						pageSize: this.pagination.pageSize					}										// æ·»åŠ çŠ¶æ€ç­›é€‰					if (this.activeStatus !== '') {						queryParams.status = this.activeStatus					}										console.log('ğŸ“‹ åŠ è½½è®¢å•åˆ—è¡¨ - ç”¨æˆ·ID:', this.currentUserId, 'èº«ä»½:', this.userRole, 'æŸ¥è¯¢å‚æ•°:', queryParams)										// ä½¿ç”¨æ–°çš„APIæ–¹æ³•ï¼Œä¼ é€’ç”¨æˆ·ID					const result = await orderService.getOrderListByUserId(this.currentUserId, queryParams)					console.log('âœ… è®¢å•åˆ—è¡¨å“åº”:', result)										// å¤„ç†è¿”å›æ•°æ®æ ¼å¼					let list = []					let total = 0										if (Array.isArray(result)) {						list = result						total = result.length					} else if (result && result.records) {						list = result.records						total = result.total					} else if (result && result.list) {						list = result.list						total = result.total					} else if (result && result.data) {						list = result.data.records || result.data.list || []						total = result.data.total || 0					}										// ä¸ºæ¯ä¸ªè®¢å•è·å–é¡¹ç›®è¯¦æƒ…å’Œè®¾è®¡å¸ˆä¿¡æ¯					console.log('ğŸ”„ å¼€å§‹è·å–è®¢å•å¯¹åº”çš„è¯¦ç»†ä¿¡æ¯...')					const ordersWithDetails = []					for (const order of list) {						let projectInfo = {}						let contractorInfo = {}												// è·å–é¡¹ç›®è¯¦æƒ…						if (order.projectId) {							try {								projectInfo = await this.getProjectDetail(order.projectId) || {}							} catch (error) {								console.error(`è·å–è®¢å• ${order.orderId} çš„é¡¹ç›®è¯¦æƒ…å¤±è´¥:`, error)							}						}												// è·å–è®¾è®¡å¸ˆä¿¡æ¯						if (order.contractorId) {							try {								contractorInfo = await this.getDesignerInfo(order.contractorId) || {}							} catch (error) {								console.error(`è·å–è®¢å• ${order.orderId} çš„è®¾è®¡å¸ˆä¿¡æ¯å¤±è´¥:`, error)							}						}												ordersWithDetails.push({							...order,							projectInfo,							contractorInfo						})					}					console.log('âœ… è®¢å•æ•°æ®æ•´åˆå®Œæˆ:', ordersWithDetails)										// æ›´æ–°æ•°æ®					if (this.pagination.pageNum === 1) {						this.orderList = ordersWithDetails					} else {						this.orderList = [...this.orderList, ...ordersWithDetails]					}										// æ›´æ–°åˆ†é¡µä¿¡æ¯					this.pagination.total = total					this.hasMore = this.orderList.length < total										// æ›´æ–°çŠ¶æ€ç»Ÿè®¡					this.updateStatusCount()									} catch (error) {					console.error('âŒ åŠ è½½è®¢å•åˆ—è¡¨å¤±è´¥:', error)					uni.showToast({						title: error.message || 'åŠ è½½å¤±è´¥',						icon: 'none'					})				} finally {					this.loading = false					this.refreshing = false				}			},						